import { FieldError, useForm } from 'react-hook-form';
import { Dispatch, SetStateAction, useEffect } from 'react';
import { PersonalInfosInterface } from './utils/personalInfos.zod.interface';
import { FormFieldConstructor } from '../../../../../components/common/Form/form';
import { userSchema } from '../../../../../validators/user.validator';
import { zodResolver } from '@hookform/resolvers/zod';
import { States } from './utils/States';
import { SelectInput } from '../../../../../components/common/Select/SelectInput';

interface PersonalInfosProps {
  steps: {
    setCurrent: Dispatch<SetStateAction<number>>;
    current: number;
  };

  form: {
    setValues: Function;
    values: any;
  };
}

export function PersonalInfosStep({ steps, form }: PersonalInfosProps) {
  const {
    register,
    handleSubmit,
    formState: { errors },
    setError,
    setValue,
    watch,
  } = useForm<PersonalInfosInterface>({
    resolver: zodResolver(userSchema),
    mode: 'onTouched',
  });

  async function onSubmit(data: PersonalInfosInterface) {
    const adjustedData = { ...data, username: data.email };
    form.setValues(adjustedData);
    steps.setCurrent(steps.current + 1);
  }

  const FormField = FormFieldConstructor<PersonalInfosInterface>();

  return (
    <form className="" onSubmit={handleSubmit(onSubmit)}>
      <FormField
        name="Nome"
        register={register}
        setError={setError}
        error={errors?.name}
        inputProps={{
          placeholder: 'Nome completo',
        }}
      />
      <FormField
        name="E-mail"
        register={register}
        setError={setError}
        error={errors?.email}
        inputProps={{
          type: 'email',
          placeholder: 'Seu Email',
        }}
      />
      <FormField
        name="Username"
        register={register}
        setError={setError}
        error={errors?.username}
        inputProps={{
          type: 'text',
          placeholder: 'Username',
        }}
      />
      <FormField
        name="Snha"
        register={register}
        setError={setError}
        error={errors?.password}
        inputProps={{
          type: 'password',
          placeholder: 'Sua senha',
        }}
      />
      <FormField
        name="confirm"
        register={register}
        setError={setError}
        error={errors?.confirm}
        inputProps={{
          placeholder: 'Confirme sua senha',
          type: 'password',
        }}
      />
      <FormField
        name="logradouro"
        register={register}
        setError={setError}
        error={errors?.logradouro}
        inputProps={{
          placeholder: 'Logradouro',
          type: 'text',
        }}
      />

      <FormField
        name="numero"
        register={register}
        setError={setError}
        error={errors?.numero}
        inputProps={{
          placeholder: 'NÃºmero',
          type: 'number',
        }}
      />

      <FormField
        name="bairro"
        register={register}
        setError={setError}
        error={errors?.bairro}
        inputProps={{
          placeholder: 'Bairro',
          type: 'text',
        }}
      />

      <FormField
        name="localidade"
        register={register}
        setError={setError}
        error={errors?.localidade}
        inputProps={{
          placeholder: 'Localidade',
          type: 'text',
        }}
      />

      <SelectInput
        register={register}
        setError={setError}
        name="uf"
        items={States}
        className="col-span-1"
        error={errors?.uf as FieldError | undefined}
      />

      <FormField
        name="cep"
        register={register}
        setError={setError}
        error={errors?.cep}
        inputProps={{
          placeholder: 'CEP',
          type: 'text',
        }}
      />

      <FormField
        name="complemento"
        register={register}
        setError={setError}
        error={errors?.complemento}
        inputProps={{
          placeholder: 'Complemento',
          type: 'text',
        }}
      />
      <button className="">PrÃ³ximo</button>
    </form>
  );
}
