import { useNavigate } from 'react-router-dom';

export function Button({
  className,
  text,
  prefix,
  sufix,
  onClick,
  redirectTo, // Prop opcional
  ...props
}: IButtonProps) {
  const navigate = useNavigate();

  const handleClick = (e: React.MouseEvent<HTMLButtonElement, MouseEvent>) => {
    if (onClick) {
      onClick(e);
    }
    if (redirectTo) {
      navigate(redirectTo);
    }
  };

  return (
    <button type="button" {...props} className={`${styles.button__card} ${className || ''}`} onClick={handleClick}>
      {prefix && prefix}
      {text}
      {sufix && sufix}
    </button>
  );
}
