import { InputHTMLAttributes } from 'react';
import {
  FieldError,
  FieldValues,
  Path,
  UseFormRegister,
  UseFormSetError,
} from 'react-hook-form';
import styles from './styles.module.scss'

interface FormFieldProps<T extends FieldValues> {
  error: FieldError | undefined;
  register: UseFormRegister<T>;
  setError: UseFormSetError<T>;
  name: Path<T>;
  inputClassName?: string;
  containerClassName?: string;
  inputProps?: InputHTMLAttributes<HTMLInputElement>;
}

function FormField<T extends FieldValues>(props: FormFieldProps<T>) {
  return (
    <div className={`form-control ${props.containerClassName}`}>
      <label className="label">
        <span>{props.name}</span>
      </label>

      <input
        {...props.register(props.name)}
        {...props.inputProps}
        className={`input ${props.inputClassName} ${
          props.error ? 'input-error' : 'input-bordered'
        }`}
      />
      <span className="text-error">{props.error?.message}</span>
    </div>
  );
}

export function FormFieldConstructor<T extends FieldValues>() {
  return FormField<T>;
}
